import 'package:easy_localization/easy_localization.dart';
import 'package:featlink_app/generated/locale_keys.g.dart';
import 'package:featlink_app/src/components/app_navigation_bottom_sheet.dart';
import 'package:featlink_app/src/config/app_colors.dart';
import 'package:featlink_app/src/resources/app_assets.dart';
import 'package:featlink_app/src/screens/swipes_zone/widget/landing_page.dart';
import 'package:featlink_app/src/screens/swipes_zone/widget/progress_bar_indicator.dart';
import 'package:flutter/material.dart';
import 'package:flutter_tindercard_plus/flutter_tindercard_plus.dart';
import 'package:heart_overlay/heart_overlay.dart';

class SwipesZoneScreen extends StatefulWidget {
  const SwipesZoneScreen({super.key});

  @override
  SwipesZoneScreenState createState() => SwipesZoneScreenState();
}

class SwipesZoneScreenState extends State<SwipesZoneScreen>
    with TickerProviderStateMixin {
  late HeartOverlayController heartOverlayController;
  List<String> images = [
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
  ];
  CardController controller = CardController();
  bool showDislikeIcon = false;
  bool showLikeIcon = false;
  @override
  void initState() {
    super.initState();
    heartOverlayController = HeartOverlayController();
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        leading: Padding(
          padding: const EdgeInsets.all(8.0),
          child: Image.asset(
            AppAssets.imageLogo2,
            width: 20,
          ),
        ),
        title: Text(
          LocaleKeys.swipes_zone_screen_app_bar_title.tr(),
          style: const TextStyle(
            color: AppColors.primary,
            fontSize: 16,
            fontWeight: FontWeight.bold,
          ),
        ),
        actions: [
          IconButton(onPressed: () {}, icon: const Icon(Icons.tune_rounded)),
          Padding(
            padding: const EdgeInsets.only(right: 35.0),
            child: Stack(
              children: [
                IconButton(
                  icon: const Icon(Icons.notifications),
                  onPressed: () {
                    // Action pour l'icône de notification
                  },
                ),
                Positioned(
                  right: 0,
                  child: Container(
                    padding: const EdgeInsets.all(2),
                    decoration: BoxDecoration(
                      color: Colors.red,
                      borderRadius: BorderRadius.circular(10),
                    ),
                    constraints: const BoxConstraints(
                      minWidth: 20,
                      minHeight: 20,
                    ),
                    child: const Text(
                      '3', // Remplacez par le nombre de notifications
                      style: TextStyle(
                        color: Colors.white,
                        fontSize: 12,
                      ),
                      textAlign: TextAlign.center,
                    ),
                  ),
                ),
              ],
            ),
          ),
        ],
      ),
      body: SizedBox(
        height: MediaQuery.of(context).size.height * 0.85,
        child: Stack(
          children: [
            Center(
              child: TinderSwapCard(
                animDuration: 400,
                totalNum: images.length,
                swipeEdge: 8.0,
                maxWidth: MediaQuery.of(context).size.width,
                maxHeight: MediaQuery.of(context).size.height,
                minWidth: MediaQuery.of(context).size.width * 0.9,
                minHeight: MediaQuery.of(context).size.height * 0.9,
                cardBuilder: (context, index) => Card(
                  child: Stack(
                    children: [
                      ClipRRect(
                        borderRadius: BorderRadius.circular(10),
                        child: Stack(
                          children: [
                            Image.network(
                              images[index],
                              fit: BoxFit.cover,
                              width: double.infinity,
                              height: double.infinity,
                              errorBuilder: (context, error, stackTrace) {
                                return Text(
                                  LocaleKeys.swipes_zone_screen_error_image
                                      .tr(),
                                );
                              },
                            ),
                            Positioned(
                              top: 20,
                              right: 20,
                              child: Container(
                                width: 20,
                                height: 20,
                                decoration: BoxDecoration(
                                  color: AppColors.primary,
                                  borderRadius: BorderRadius.circular(10),
                                ),
                              ),
                            ),
                            Positioned(
                              bottom: 0,
                              left: 0,
                              right: 0,
                              child: Container(
                                height: 160,
                                decoration: const BoxDecoration(
                                  gradient: LinearGradient(
                                    colors: [Colors.black, Colors.transparent],
                                    begin: Alignment.bottomCenter,
                                    end: Alignment.topCenter,
                                  ),
                                ),
                                padding: const EdgeInsets.all(10),
                                child: Column(
                                  crossAxisAlignment: CrossAxisAlignment.start,
                                  children: [
                                    Text(
                                      LocaleKeys.swipes_zone_screen_user_name
                                          .tr(), // Remplacez par le nom et l'âge de l'utilisateur
                                      style: const TextStyle(
                                        color: Colors.white,
                                        fontSize: 16,
                                        fontWeight: FontWeight.bold,
                                      ),
                                    ),
                                    Text(
                                      LocaleKeys
                                          .swipes_zone_screen_user_description
                                          .tr(), // Remplacez par la description de l'utilisateur
                                      style: const TextStyle(
                                        color: Colors.white,
                                        fontSize: 9,
                                      ),
                                    ),
                                  ],
                                ),
                              ),
                            ),
                          ],
                        ),
                      ),
                      if (showDislikeIcon)
                        Center(
                          child: Container(
                            decoration: BoxDecoration(
                              color: Colors.white,
                              borderRadius: BorderRadius.circular(100),
                            ),
                            padding: const EdgeInsets.all(15),
                            child: const Icon(
                              Icons.close_rounded,
                              color: Colors.orange,
                              size: 70,
                            ),
                          ),
                        ),
                      if (showLikeIcon)
                        Center(
                          child: Container(
                            decoration: BoxDecoration(
                              color: Colors.white,
                              borderRadius: BorderRadius.circular(100),
                            ),
                            padding: const EdgeInsets.all(15),
                            child: const Icon(
                              Icons.favorite,
                              color: AppColors.primary,
                              size: 60,
                            ),
                          ),
                        ),
                      HeartOverlay(
                        controller: heartOverlayController,
                        duration: const Duration(seconds: 3),
                        icon: const Icon(
                          Icons.favorite,
                          color: Colors.pink,
                          size: 150,
                        ),
                      ),
                      ProgressBarIndicator(images: images, index: index),
                      Positioned(
                        bottom: 20,
                        left: 10,
                        right: 10,
                        child: Padding(
                          padding: const EdgeInsets.symmetric(vertical: 20.0),
                          child: Row(
                            mainAxisAlignment: MainAxisAlignment.spaceEvenly,
                            children: [
                              IconButton(
                                icon: Container(
                                  width: 45,
                                  height: 45,
                                  decoration: BoxDecoration(
                                    border:
                                        Border.all(color: AppColors.primary),
                                    borderRadius: BorderRadius.circular(50),
                                  ),
                                  padding: const EdgeInsets.all(6),
                                  child: Transform(
                                    alignment: Alignment.center,
                                    transform: Matrix4.rotationY(3.14159),
                                    child: const Icon(
                                      Icons.refresh_outlined,
                                      color: AppColors.primary,
                                      size: 20,
                                    ),
                                  ),
                                ),
                                onPressed: () {
                                  // Action for refresh button
                                },
                              ),
                              IconButton(
                                icon: Container(
                                  width: 50,
                                  height: 50,
                                  decoration: BoxDecoration(
                                    borderRadius: BorderRadius.circular(50),
                                    border: Border.all(color: Colors.red),
                                  ),
                                  padding: const EdgeInsets.all(6),
                                  child: const Icon(
                                    Icons.close,
                                    color: Colors.red,
                                    size: 20,
                                  ),
                                ),
                                onPressed: () {
                                  controller.triggerLeft();
                                },
                              ),
                              IconButton(
                                icon: Container(
                                  width: 45,
                                  height: 45,
                                  decoration: BoxDecoration(
                                    borderRadius: BorderRadius.circular(50),
                                    border: Border.all(color: Colors.blue),
                                  ),
                                  padding: const EdgeInsets.all(6),
                                  child: const Icon(
                                    Icons.mail,
                                    color: Colors.blue,
                                    size: 20,
                                  ),
                                ),
                                onPressed: () {
                                  // Action for mail button
                                },
                              ),
                              Container(
                                width: 50,
                                height: 50,
                                decoration: BoxDecoration(
                                  borderRadius: BorderRadius.circular(50),
                                  color: AppColors.primary,
                                ),
                                child: IconButton(
                                  icon: const Icon(
                                    Icons.favorite,
                                    color: AppColors.myWhite,
                                    size: 25,
                                  ),
                                  onPressed: () {
                                    setState(() {
                                      setState(() {
                                        heartOverlayController.showIcon(
                                          offset: Offset(
                                            MediaQuery.of(context).size.width /
                                                2,
                                            MediaQuery.of(context).size.height /
                                                2,
                                          ),
                                        );
                                      });
                                    });
                                    controller.triggerRight();
                                  },
                                ),
                              ),
                            ],
                          ),
                        ),
                      ),
                    ],
                  ),
                ),
                cardController: controller,
                swipeUpdateCallback:
                    (DragUpdateDetails details, Alignment align) {
                  setState(() {
                    if (align.x < 0) {
                      // Swiping left
                      showDislikeIcon = true;
                    } else if (align.x > 0) {
                      showLikeIcon = true;
                      heartOverlayController.showIcon(
                        offset: Offset(
                          MediaQuery.of(context).size.width / 2,
                          MediaQuery.of(context).size.height / 2,
                        ),
                      );
                    } else if (align.x == 0) {
                      showDislikeIcon = false;
                      showLikeIcon = false;
                    }
                  });
                },
                swipeCompleteCallback:
                    (CardSwipeOrientation orientation, int index) {
                  if (orientation == CardSwipeOrientation.right ||
                      orientation == CardSwipeOrientation.left) {
                    setState(() {
                      showDislikeIcon = false;
                      showLikeIcon = false;
                    });
                    if (index == images.length - 1) {
                      Navigator.of(context).pushReplacement(
                        MaterialPageRoute(
                          builder: (context) => const LandingPage(),
                        ),
                      );
                    }
                  }
                },
              ),
            ),
          ],
        ),
      ),
      floatingActionButton: const AppNavigationBottomSheet(),
      floatingActionButtonLocation: FloatingActionButtonLocation.centerDocked,
    );
  }
}
